import BGCEffect from '../src/index.js'

describe("BGCEffect()", () => {

    test('BGCEffect will be successfully created if supplied an element', () => {
        
        let effect = new BGCEffect( document.createElement("div") )
        expect( effect.element ).toBeDefined()
        
    });

    test('BGCEffect will be successfully created if document body is present', () => {
        
        let effect = new BGCEffect( )
        expect( effect.element ).toBeDefined()
        
    });

})